
// Modelo Artifact
exports.Artifact = function( params ){
	var defaults = {

		/*Objeto que contiene el sprite de THREE.JS*/
		"sprite":null,

		/* Id de identificacion unico*/
		"id":null,

		/*Angulo del Being*/
		"rotation":{
			"x":0,
			"y":0
		},

		"type":"artifact",

		/*Posicion del Being*/
		"position":{
			"x":0,
			"y":0
		},

		/*Propiedad que define si el objeto puede Being atravesado*/
		"solid":false,

		// Indica si se puede usar el objeto
		"usable":false,

		// Indica si una vez usado el objeto desaparece
		"consumable":false,

		// Cantidad de items
		"count" : 1,

	}




	/*Mezcla de los defaults con los parametros pasados al objeto*/
	merge( defaults , params , this );

	//----------------------------------------------------------------------------------
	// Genera un id aleatorio que no exista.
	//----------------------------------------------------------------------------------
	this.generateId = function(){
		var id;
		do{
			id = 'a' + parseInt( Math.random() * 10000 );
		}while( id in world.createdObjects );
		return id;
	}

}
