exports.Tierra = function( params ){

	// Props del hijo (con mas valor que las del Padre)
	var defaults = {
		"sprite":{
			"images":{
				"TierraC":"../public/sprites/tiles-juego_47.jpg",
				"TierraNEAgua":"../public/sprites/tiles-juego_42.jpg",
				"TierraSEAgua":"../public/sprites/tiles-juego_54.jpg",
				"TierraSOAgua":"../public/sprites/tiles-juego_52.jpg",
				"TierraNOAgua":"../public/sprites/tiles-juego_40.jpg",

				"AguaNTierra":"../public/sprites/tiles-juego_38.jpg",
				"AguaETierra":"../public/sprites/tiles-juego_45.jpg",
				"AguaSTierra":"../public/sprites/tiles-juego_41.jpg",
				"AguaOTierra":"../public/sprites/tiles-juego_43.jpg",

				"AguaNETierra":"../public/sprites/tiles-juego_39.jpg",
				"AguaSETierra":"../public/sprites/tiles-juego_51.jpg",
				"AguaSOTierra":"../public/sprites/tiles-juego_49.jpg",
				"AguaNOTierra":"../public/sprites/tiles-juego_37.jpg",
			},
			"hasToCalculatePosition":true,
			"width": 10,
			"height": 10,
			"rotation": -Math.PI/2,
			"offsetHeight":0
		},
		"name": 'tierra',
		"descripcion": "Tierra com√∫n y silvestre",
		"solid": false,
		"specie":"TierraC",

	}

	// Hereda del padre y le agrega defaults.
	extend( this , Terrain , defaults );

	// Sobreescribe las propiedades con los valores pasados en params cuando se instancia.
	merge( this , params , this );

	// Se agrega en el mapa
	global.world.getTile( this.position ).append({ 'terrain' : this })
}
