// Modelo User
global.User = function( options ){
	var options = options || {}

	// Definicion de parametros por default
	var defaults = {
		'nombre':'',
		'posicion':{'x':0 , 'y':0}
	}

	// Funcion que mergea las opciones con los defaults
	var extend = function(self){
		for( prop in defaults ){
			self[prop] = options[prop] || defaults[prop] || null;
		}
	}
  	extend(this);


}
