exports.move = function( keyname , id ){



	console.log(id+' se movio!')
	switch(keyname){
		case 'up':
			users[id].pos.y-=15;
			break;
		case 'right':
			users[id].pos.x+=15;
			break;
		case 'down':
			users[id].pos.y+=15;
			break;
		case 'left':
			users[id].pos.x-=15;
			break;
	}
	io.sockets.emit("actualizar_pos",users)
};

exports.jump = function( keyname , id ){
	console.log('se llamo a la funcion jump()')
	switch(keyname){
		case 'enter':
			users[id].pos.y-=50;
			break;
	}
	io.sockets.emit("actualizar_pos",users)
};